#Question 1
def how_many_handle(data)
    puts data.length
end 

#Question 2
def short_handle(data)
    min = []
    data.each do |i|
        min.append(i.length)
    end
    data.each do |i|
        if i.length == min.min
            puts i
        end 
    end
end 

#Question 3
def five_car(data)
    five = []
    data.each do |i|
        if (i.length - 1) == 5
            five.append(i)
        end
    end
    puts five.length
end

#Question 4
def maj(data)
    maj = []
    data.each do |i|
        if i.slice(1).capitalize == i.slice(1)
            maj.append(i)
        end 
    end 
    puts maj.length
end

#Question 5
def sort(data)
    puts data.sort
end

#Question 6
def sort_size(data)
    puts data.sort_by {|element| element.length}
end

#Question 7
def penser(data)
    puts data.index("@epenser")
end

#Question 8
def repartition(data)
    distrib = {}
    20.times do |i|
        distrib[i] = 0
    end

    distrib.each do |k,v|
        data.each do |i|
            if (i.length - 1) == k
            distrib[k] += 1
            end 
        end
    end
    distrib.each do |k,v|
        if v != 0
            puts "Il y a #{v} nom(s) contenant #{k} caractères"
        end
    end
end

